    public function test__construct()
    {   
        $this->assertInstanceOf(Googlon::Class, $this->googlon);
    }

    public function testGetValue()
    {
        $this->assertEquals($this->text, $this->googlon->getValue());
    }

    public function testGetPrepositions()
    {
        $this->assertEquals(true, is_int($this->googlon->getPrepositions()) );
        $this->assertEquals(22, $this->googlon->getPrepositions());
    }

    public function testGetVerbs()
    {
        $this->assertEquals(true, is_array( $this->googlon->getVerbs() ) );
        $this->assertEquals(["first_person" => 37,"total" => 160], $this->googlon->getVerbs());
    }

    public function testGetNumbers()
    {
        $this->assertEquals(true, is_array( $this->googlon->getNumbers() ) );
        $this->assertEquals(["beautiful" => 77,"total" => 600], $this->googlon->getNumbers());
    }

    public function testIsBarType()
    {
        $this->assertEquals(true, $this->googlon->isBarType('p'));
        $this->assertEquals(false, $this->googlon->isBarType('c'));
    }

    public function testIsFooType()
    {
        $this->assertEquals(true, $this->googlon->isFooType('c'));
        $this->assertEquals(false, $this->googlon->isFooType('p'));
    }

    public function testIsPreposition()
    {
        $this->assertEquals(true, $this->googlon->isPreposition('ktqc'));
    }

    public function testIsVerb()
    {
        $this->assertEquals(true, $this->googlon->isVerb('rtvxwlkgp'));
        $this->assertEquals(false, $this->googlon->isVerb('kjklptqc'));
    }

    public function testIsFirstPerson()
    {
        $this->assertEquals(true, $this->googlon->isFirstPerson('cgvxwlkgp'));
        $this->assertEquals(false, $this->googlon->isFirstPerson('rtvxwlkgp'));
    }

    public function testGetWordNumValue()
    {
        $this->assertEquals(541663264, $this->googlon->getWordNumValue("ngvzwxt"));
    }

    public function testIsBeautifulNumber()
    {   
        $this->assertEquals(true, $this->googlon->isBeautifulNumber(541663260));
        $this->assertEquals(false, $this->googlon->isBeautifulNumber(541663264));
